class Solution {
    public int findLengthOfShortestSubarray(int[] arr) {
        Deque<Integer> deque = new LinkedList<>();
        int maxSize = 0;

        for (int num : arr) {
            while(!deque.isEmpty() && deque.peekLast() > num) {
                deque.pollLast();
            }
            deque.offerLast(num);
            maxSize = Math.max(maxSize, deque.size());
        }
        return arr.length - maxSize;
    }
}
