class Solution {
    public int[][] findFarmland(int[][] land) {
        // 遍历标记为原地
        int height = land.length;
        int width = land[0].length;
        List<int[]> ans = new ArrayList<>();

        for (int h = 0; h < height; h++) {
            for (int w = 0; w < width; w++) {
                int value = land[h][w];
                if(value == 1) {
                    // 此位置一定为左上角，BFS获取右下角
                    int[] bfs = bfs(land, h, w);
                    ans.add(new int[]{h, w, bfs[0], bfs[1]});
                }
            }
        }
        return ans.toArray(new int[0][0]);
    }

    /**
     * 从(x,y)开始，BFS遍历数组，修改走过的地方为-1，并返回最后的位置
     * @param land
     * @param x
     * @param y
     * @return
     */
    private int[] bfs(int[][] land, int x, int y) {
        Queue<int[]> queue = new LinkedList<>();
        queue.offer(new int[]{x, y});
        land[x][y] = -1;
        int[] ans = null;
        int size;
        int height = land.length;
        int width = land[0].length;

        while((size = queue.size()) != 0) {
            for (int i = 0; i < size; i++) {
                ans = queue.poll();
                // 获取右边和下面
                if(ans[0] != height - 1) {
                    if(land[ans[0] + 1][ans[1]] == 1) {
                        queue.offer(new int[]{ans[0] + 1, ans[1]});
                        land[ans[0] + 1][ans[1]] = -1;
                    }
                }
                if(ans[1] != width - 1) {
                    if(land[ans[0]][ans[1] + 1] == 1) {
                        queue.offer(new int[]{ans[0], ans[1] + 1});
                        land[ans[0]][ans[1] + 1] = -1;
                    }
                }
            }
        }
        return ans;
    }
}
